import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Cards = () => {
  const [books, setBooks] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');
  const [updateFormData, setUpdateFormData] = useState({
    title: '',
    author: '',
    imageUrl: '',
    description: '',
    genre: '',
  });

  useEffect(() => {
    // Fetch books from your server's API endpoint (e.g., /api/books)
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/books');
        setBooks(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBooks();
  }, []);

  const handleDeleteBook = async (bookId) => {
    try {
      // Send a DELETE request to delete the book by its ID
      await axios.delete(`http://localhost:3001/api/books/${bookId}`);

      // After successful deletion, update the list of books on the client-side
      setBooks((prevBooks) => prevBooks.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdateFormDataChange = (e) => {
    const { name, value } = e.target;
    setUpdateFormData({ ...updateFormData, [name]: value });
  };

  const handleUpdateBook = async (bookId) => {
    try {
      // Send a PUT request to update the book by its ID with the updated data
      await axios.put(`http://localhost:3001/api/books/${bookId}`, updateFormData);

      // After successful update, reload the list of books
      const response = await axios.get('http://localhost:3001/api/books');
      setBooks(response.data);

      // Clear the update form data
      setUpdateFormData({
        title: '',
        author: '',
        imageUrl: '',
        description: '',
        genre: '',
      });
    } catch (error) {
      console.error(error);
    }
  };

  // Filter the books based on selectedGenre and searchQuery
  const filteredBooks = books.filter((book) => {
    const matchesGenre = selectedGenre === 'All' || book.genre === selectedGenre;
    const lowerCaseSearchQuery = searchQuery.toLowerCase();
    const matchesTitle = book.title.toLowerCase().includes(lowerCaseSearchQuery);
    const matchesAuthor = book.author.toLowerCase().includes(lowerCaseSearchQuery);
    return matchesGenre && (matchesTitle || matchesAuthor);
  });

  return (
    <div className="container mx-auto px-4">
      <div className="mb-6">
        {/* Search input */}
        <input
          type="text"
          placeholder="Search by title or author..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="form-input px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-opacity-50"
        />
        {/* Filter buttons */}
        <div className="mt-4">
          {['All', 'Mystery', 'Science Fiction', 'Romance', 'Fantasy'].map((genre) => (
            <button
              key={genre}
              className={`px-4 py-2 rounded-md text-white ${selectedGenre === genre ? 'bg-blue-600' : 'bg-gray-500'} hover:bg-blue-700`}
              onClick={() => setSelectedGenre(genre)}
            >
              {genre}
            </button>
          ))}
        </div>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {filteredBooks.map((book) => (
          <div key={book._id} className="bg-white shadow-md rounded-lg overflow-hidden">
            {/* Card content */}
            <img src={book.imageUrl} className="w-full h-48 object-cover" alt={book.title} />
            <div className="p-4">
              <h5 className="text-xl font-semibold mb-2">{book.title}</h5>
              <p className="text-gray-700 mb-2">Author: {book.author}</p>
              <p className="text-gray-700 mb-4">Genre: {book.genre}</p>
              <Link to="/dub" className="inline-block px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Read now</Link>
            </div>
            <div className="relative">
              {/* Kebab menu in the top-right corner */}
              <div className="absolute top-2 right-2">
                <div className="relative">
                  <button
                    className="bg-gray-300 text-gray-800 rounded-full p-2 focus:outline-none"
                    id={`kebab-menu-${book._id}`}
                    aria-expanded="false"
                  >
                    ...
                  </button>
                  <div className="absolute right-0 mt-2 w-48 bg-white border border-gray-300 rounded shadow-lg hidden" aria-labelledby={`kebab-menu-${book._id}`}>
                    <div className="p-2">
                      <form onSubmit={() => handleUpdateBook(book._id)} className="space-y-4">
                        <div>
                          <input
                            type="text"
                            className="form-input w-full"
                            placeholder="Title"
                            name="title"
                            value={updateFormData.title}
                            onChange={handleUpdateFormDataChange}
                          />
                        </div>
                        <div>
                          <input
                            type="text"
                            className="form-input w-full"
                            placeholder="Author"
                            name="author"
                            value={updateFormData.author}
                            onChange={handleUpdateFormDataChange}
                          />
                        </div>
                        <div>
                          <input
                            type="text"
                            className="form-input w-full"
                            placeholder="Image URL"
                            name="imageUrl"
                            value={updateFormData.imageUrl}
                            onChange={handleUpdateFormDataChange}
                          />
                        </div>
                        <div>
                          <textarea
                            className="form-textarea w-full"
                            placeholder="Description"
                            name="description"
                            value={updateFormData.description}
                            onChange={handleUpdateFormDataChange}
                          />
                        </div>
                        <div>
                          <input
                            type="text"
                            className="form-input w-full"
                            placeholder="Genre"
                            name="genre"
                            value={updateFormData.genre}
                            onChange={handleUpdateFormDataChange}
                          />
                        </div>
                        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Update Book</button>
                      </form>
                    </div>
                    <button
                      className="w-full bg-yellow-400 text-black px-4 py-2 rounded hover:bg-yellow-500"
                      onClick={() => handleDeleteBook(book._id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Cards;
